import React, { useState } from 'react';
import './App.css';
import IdeationForm from './components/IdeationForm';
import MindMap from './components/MindMap';
import DraggableContainer from './components/DraggableContainer';

function App() {
  const [mindMapData, setMindMapData] = useState(null);

  const handleGenerate = async ({ targetAudience, problem }) => {
    // TODO: Replace with actual API call
    const question = `How Might We help ${targetAudience} ${problem}?`;

    // Format concepts with ids and text properties
    const conceptsData = [
      { id: 'concept-0', text: 'Concept 1' },
      { id: 'concept-1', text: 'Concept 2' },
      { id: 'concept-2', text: 'Concept 3' },
      { id: 'concept-3', text: 'Concept 4' },
      { id: 'concept-4', text: 'Concept 5' }
    ];

    setMindMapData({ centralQuestion: question, concepts: conceptsData });
  };

  return (
    <DraggableContainer>
      <div className="App">
        <div className="App-content">
          <header className="App-header">
            <h1>AIdeator</h1>
          </header>
          <main className="App-main">
            <IdeationForm onGenerate={handleGenerate} />
            {mindMapData && (
              <MindMap 
                centralQuestion={mindMapData.centralQuestion}
                concepts={mindMapData.concepts}
              />
            )}
          </main>
        </div>
      </div>
    </DraggableContainer>
  );
}

export default App;
