import React, { useState, useEffect, useRef, useCallback } from 'react';
import './MindMap.css';

const MindMap = ({ centralQuestion, concepts }) => {
  const [positions, setPositions] = useState({});
  const [dragging, setDragging] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [activeNodes, setActiveNodes] = useState(concepts.map(() => true));
  const [expandedNodeIndex, setExpandedNodeIndex] = useState(null);
  const [expandedConcepts, setExpandedConcepts] = useState([]);
  const mindMapRef = useRef(null);
  const svgRef = useRef(null);
  const conceptRefs = useRef({});
  const centralQuestionRef = useRef(null);

  // Initialize concept refs
  useEffect(() => {
    conceptRefs.current = {};
    concepts.forEach((_, index) => {
      conceptRefs.current[`concept-${index}`] = null;
    });
  }, [concepts]);

  // Calculate initial positions in a circle around the center
  useEffect(() => {
    const radius = Math.min(window.innerWidth, window.innerHeight) / 4;
    const centerX = window.innerWidth / 2;
    const centerY = window.innerHeight / 2;

    const newPositions = {};

    concepts.forEach((_, index) => {
      const angle = (2 * Math.PI * index) / concepts.length;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);

      newPositions[`concept-${index}`] = { x, y };
    });

    setPositions(newPositions);
  }, [concepts]);

  // Expand a node with 5 new child nodes
  const expandNode = (index) => {
    console.log("Expanding node", index);
    setExpandedNodeIndex(index);

    // Create 5 new concept nodes with titles and descriptions
    const newConcepts = [
      {
        title: "Fear of letting others down",
        description: "Social and professional pressures can make people fear judgment from peers."
      },
      {
        title: "Concept B",
        description: "A detailed description of concept B and its implications."
      },
      {
        title: "Concept C",
        description: "A detailed description of concept C and its implications."
      },
      {
        title: "Concept D",
        description: "A detailed description of concept D and its implications."
      },
      {
        title: "Concept E",
        description: "A detailed description of concept E and its implications."
      }
    ];

    setExpandedConcepts(newConcepts);

    // Create positions for the new nodes following the parent's side
    const parentPos = positions[`concept-${index}`];
    const newPositions = { ...positions };

    // Determine which side the parent node is on relative to the center
    // This uses the standard layout: top, left, right, bottom-left, bottom-right
    let direction;

    if (index === 0) {
      // Top node - place children above it
      direction = 'top';
    } else if (index === 1) {
      // Left node - place children to the left
      direction = 'left';
    } else if (index === 2) {
      // Right node - place children to the right
      direction = 'right';
    } else if (index === 3) {
      // Bottom Left node - place children below and to the left
      direction = 'bottom-left';
    } else if (index === 4) {
      // Bottom Right node - place children below and to the right
      direction = 'bottom-right';
    }

    console.log("Direction for expanded nodes:", direction);

    // Position nodes based on the parent's position/direction
    switch (direction) {
      case 'top':
        // Arrange horizontally above the parent
        for (let i = 0; i < newConcepts.length; i++) {
          const xOffset = (i - 2) * 120; // -240, -120, 0, 120, 240
          newPositions[`expanded-${index}-${i}`] = {
            x: parentPos.x + xOffset,
            y: parentPos.y - 150
          };
        }
        break;

      case 'left':
        // Arrange vertically to the left - increase spacing
        for (let i = 0; i < newConcepts.length; i++) {
          const yOffset = (i - 2) * 100; // -200, -100, 0, 100, 200
          newPositions[`expanded-${index}-${i}`] = {
            x: parentPos.x - 250,
            y: parentPos.y + yOffset
          };
        }
        break;

      case 'right':
        // Arrange vertically to the right - increase spacing
        for (let i = 0; i < newConcepts.length; i++) {
          const yOffset = (i - 2) * 100; // -200, -100, 0, 100, 200
          newPositions[`expanded-${index}-${i}`] = {
            x: parentPos.x + 250,
            y: parentPos.y + yOffset
          };
        }
        break;

      case 'bottom-left':
        // Arrange horizontally below and to the left
        for (let i = 0; i < newConcepts.length; i++) {
          const xOffset = (i - 2) * 120; // -240, -120, 0, 120, 240
          newPositions[`expanded-${index}-${i}`] = {
            x: parentPos.x - 100 + xOffset,
            y: parentPos.y + 150
          };
        }
        break;

      case 'bottom-right':
        // Arrange horizontally below and to the right
        for (let i = 0; i < newConcepts.length; i++) {
          const xOffset = (i - 2) * 120; // -240, -120, 0, 120, 240
          newPositions[`expanded-${index}-${i}`] = {
            x: parentPos.x + 100 + xOffset,
            y: parentPos.y + 150
          };
        }
        break;
    }

    setPositions(newPositions);
  };

  // Toggle node active state
  const toggleNodeActive = (index) => {
    // Count how many inactive nodes we currently have
    const inactiveCount = activeNodes.filter(active => !active).length;

    // If this node is already inactive, we can always make it active again
    if (!activeNodes[index]) {
      setActiveNodes(prev => {
        const newActive = [...prev];
        newActive[index] = true;
        return newActive;
      });
      // If we're activating a node, clear expansion
      if (expandedNodeIndex !== null) {
        setExpandedNodeIndex(null);
        setExpandedConcepts([]);
      }
      return;
    }

    // If this would be the 5th inactive node (all inactive), don't allow it
    if (inactiveCount === 4) {
      // Find the only remaining active node (which must be this one)
      const activeNodeIndices = activeNodes.map((active, i) => active ? i : -1).filter(i => i >= 0);
      if (activeNodeIndices.length === 1 && activeNodeIndices[0] === index) {
        console.log("Can't deactivate the last node");
        return;
      }
    }

    // Make this node inactive
    setActiveNodes(prev => {
      const newActive = [...prev];
      newActive[index] = false;
      return newActive;
    });

    // Check if we now have 4 inactive nodes (1 active)
    const newInactiveCount = inactiveCount + 1;
    if (newInactiveCount === 4) {
      // Find the one remaining active node
      const remainingActiveIndex = activeNodes.findIndex((active, i) => active && i !== index);
      if (remainingActiveIndex !== -1) {
        // Expand the remaining active node
        expandNode(remainingActiveIndex);
      }
    }
  };

  const handleMouseDown = useCallback((e, id) => {
    e.stopPropagation();

    // Get the element's bounding rectangle
    const rect = conceptRefs.current[id].getBoundingClientRect();

    // Calculate offset from mouse position to concept center
    const offsetX = e.clientX - rect.left - rect.width / 2;
    const offsetY = e.clientY - rect.top - rect.height / 2;

    setDragOffset({ x: offsetX, y: offsetY });
    setDragging(id);
  }, []);

  const handleMouseMove = useCallback((e) => {
    if (dragging) {
      e.preventDefault();
      e.stopPropagation();

      // Update position based on mouse movement, accounting for offset
      setPositions(prev => ({
        ...prev,
        [dragging]: {
          x: e.clientX - dragOffset.x,
          y: e.clientY - dragOffset.y
        }
      }));
    }
  }, [dragging, dragOffset]);

  const handleMouseUp = useCallback((e) => {
    if (dragging) {
      e.stopPropagation();
      setDragging(null);
    }
  }, [dragging]);

  // Add and remove event listeners for dragging
  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleMouseMove, handleMouseUp]);

  // Render connections between the central question and concepts
  const renderConnections = () => {
    if (!centralQuestionRef.current || !svgRef.current) return null;

    // Get SVG's bounding rect to calculate coordinates relative to it
    const svgRect = svgRef.current.getBoundingClientRect();

    // Get the central question's bounding rect
    const centralRect = centralQuestionRef.current.getBoundingClientRect();

    // Calculate central question center point relative to the SVG
    const centralX = centralRect.left + centralRect.width / 2 - svgRect.left;
    const centralY = centralRect.top + centralRect.height / 2 - svgRect.top;

    return concepts.map((_, index) => {
      const conceptId = `concept-${index}`;
      const conceptEl = conceptRefs.current[conceptId];

      if (!conceptEl) return null;

      // Get concept's bounding rect
      const conceptRect = conceptEl.getBoundingClientRect();

      // Calculate concept center point relative to the SVG
      const conceptX = conceptRect.left + conceptRect.width / 2 - svgRect.left;
      const conceptY = conceptRect.top + conceptRect.height / 2 - svgRect.top;

      return (
        <line
          key={conceptId}
          x1={centralX}
          y1={centralY}
          x2={conceptX}
          y2={conceptY}
          className="connection"
        />
      );
    });
  };

  // Update SVG connections when positions change
  useEffect(() => {
    // Force a re-render of SVG connections when positions change
    if (svgRef.current) {
      const forceUpdate = setTimeout(() => {
        setPositions(prev => ({...prev}));
      }, 50);

      return () => clearTimeout(forceUpdate);
    }
  }, [positions]);

  // Console log for debugging
  console.log("Active nodes:", activeNodes);
  console.log("Expanded node index:", expandedNodeIndex);
  console.log("Expanded concepts:", expandedConcepts);
  console.log("Positions:", positions);

  return (
    <div className="mind-map-container">
      <div className="mind-map" ref={mindMapRef}>
        <svg className="connections" ref={svgRef}>
          {renderConnections()}
        </svg>

        <div 
          className="central-question"
          ref={centralQuestionRef}
        >
          {centralQuestion}
        </div>

        {concepts.map((concept, index) => {
          const conceptId = `concept-${index}`;
          const pos = positions[conceptId] || { x: 0, y: 0 };
          const isDragging = dragging === conceptId;

          return (
            <div
              key={conceptId}
              className={`concept ${!activeNodes[index] ? 'inactive' : ''} ${isDragging ? 'dragging' : ''} ${index === expandedNodeIndex ? 'expanded' : ''}`}
              style={{
                left: `${pos.x}px`,
                top: `${pos.y}px`,
                transform: 'translate(-50%, -50%)',
                cursor: isDragging ? 'grabbing' : 'grab'
              }}
              ref={el => conceptRefs.current[conceptId] = el}
              onMouseDown={(e) => handleMouseDown(e, conceptId)}
              onClick={(e) => {
                e.stopPropagation();
                if (!dragging) {
                  toggleNodeActive(index);
                }
              }}
            >
              {concept}
            </div>
          );
        })}

        {/* Expanded Concept Nodes */}
        {expandedNodeIndex !== null && expandedConcepts.map((concept, index) => {
          const pos = positions[`expanded-${expandedNodeIndex}-${index}`];
          if (!pos) return null;

          return (
            <div
              key={`expanded-${expandedNodeIndex}-${index}`}
              className="concept expanded-child"
              data-expanded-index={`${expandedNodeIndex}-${index}`}
              style={{
                left: `${pos.x}px`,
                top: `${pos.y}px`,
                border: '2px solid #000'
              }}
            >
              <div className="concept-title">{concept.title}</div>
              {concept.description && (
                <div className="concept-description">{concept.description}</div>
              )}
              <div className="concept-icon">
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                  <path fill="none" d="M0 0h24v24H0z"/>
                  <path d="M9.973 18h4.054c.132-1.202.745-2.194 1.74-3.277.113-.122.832-.867.917-.973a6 6 0 1 0-9.37-.002c.086.107.807.853.918.974.996 1.084 1.609 2.076 1.741 3.278zM14 20h-4v1h4v-1zm-8.246-5a8 8 0 1 1 12.49.002C17.624 15.774 16 17 16 18.5V21a2 2 0 0 1-2 2h-4a2 2 0 0 1-2-2v-2.5C8 17 6.375 15.774 5.754 15z" fill="rgba(0,0,0,0.6)"/>
                </svg>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MindMap;
