import React, { useState, useRef, useEffect } from 'react';
import './MindMap.css';

const MindMap = ({ centralQuestion, concepts }) => {
  const [positions, setPositions] = useState({});
  const [draggedConcept, setDraggedConcept] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [activeNodes, setActiveNodes] = useState(concepts.map((_, i) => true));
  const [nodeRefs, setNodeRefs] = useState({});
  const mapRef = useRef(null);
  const centralRef = useRef(null);

  // Create refs for each concept node
  useEffect(() => {
    const refs = {};
    concepts.forEach((_, index) => {
      refs[`concept-${index}`] = React.createRef();
    });
    setNodeRefs(refs);
  }, [concepts]);

  // Initialize positions in a circular pattern around the center
  useEffect(() => {
    if (Object.keys(positions).length === 0) {
      const initialPositions = {};

      // Position concepts evenly in a circle around the central question
      concepts.forEach((_, index) => {
        const angle = (2 * Math.PI * index) / concepts.length;
        initialPositions[`concept-${index}`] = {
          x: Math.cos(angle) * 300,
          y: Math.sin(angle) * 200
        };
      });

      setPositions(initialPositions);
    }
  }, [concepts, positions]);

  // Handle starting to drag a concept
  const handleMouseDown = (e, conceptId) => {
    e.preventDefault();
    e.stopPropagation();

    const rect = e.currentTarget.getBoundingClientRect();
    const mapRect = mapRef.current.getBoundingClientRect();

    setDraggedConcept(conceptId);
    setDragOffset({
      x: e.clientX - (rect.left - mapRect.left),
      y: e.clientY - (rect.top - mapRect.top)
    });

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  // Handle dragging a concept
  const handleMouseMove = (e) => {
    if (!draggedConcept) return;

    e.preventDefault();
    const mapRect = mapRef.current.getBoundingClientRect();
    const newX = e.clientX - mapRect.left - dragOffset.x;
    const newY = e.clientY - mapRect.top - dragOffset.y;

    setPositions(prev => ({
      ...prev,
      [draggedConcept]: { x: newX, y: newY }
    }));
  };

  // Handle dropping a concept
  const handleMouseUp = () => {
    setDraggedConcept(null);
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  // Toggle active/inactive state of a concept when clicked
  const handleConceptClick = (index) => {
    if (draggedConcept) return; // Don't toggle if we're dragging

    setActiveNodes(prev => {
      const newActiveNodes = [...prev];
      newActiveNodes[index] = !newActiveNodes[index];
      return newActiveNodes;
    });
  };

  return (
    <div className="mind-map-container">
      <div 
        className="mind-map" 
        ref={mapRef}
      >
        <div className="central-question" ref={centralRef}>
          {centralQuestion}
        </div>

        <svg className="connections">
          {concepts.map((_, index) => {
            const pos = positions[`concept-${index}`];
            if (!pos) return null;

            // Direct line from center to concept
            return (
              <line
                key={`line-${index}`}
                x1="50%"
                y1="50%"
                x2={`calc(50% + ${pos.x}px)`}
                y2={`calc(50% + ${pos.y}px)`}
                className={!activeNodes[index] ? 'connection inactive' : 'connection'}
              />
            );
          })}
        </svg>

        {concepts.map((concept, index) => {
          const pos = positions[`concept-${index}`] || { x: 0, y: 0 };
          return (
            <div
              key={index}
              className={`concept ${!activeNodes[index] ? 'inactive' : ''} ${draggedConcept === `concept-${index}` ? 'dragging' : ''}`}
              style={{
                transform: `translate(calc(50% + ${pos.x}px), calc(50% + ${pos.y}px))`,
                cursor: draggedConcept === `concept-${index}` ? 'grabbing' : 'grab'
              }}
              ref={nodeRefs[`concept-${index}`]}
              onMouseDown={(e) => handleMouseDown(e, `concept-${index}`)}
              onClick={() => handleConceptClick(index)}
            >
              {concept}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MindMap;
