import React, { useState, useRef, useEffect } from 'react';
import './MindMap.css';

const MindMap = ({ centralQuestion, concepts }) => {
  const [positions, setPositions] = useState({});
  const [draggedConcept, setDraggedConcept] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [activeNodes, setActiveNodes] = useState(concepts.map((_, i) => true));
  const [nodeRefs, setNodeRefs] = useState({});
  const [lines, setLines] = useState([]);
  const mapRef = useRef(null);
  const centralRef = useRef(null);

  // Create refs for each concept node
  useEffect(() => {
    const refs = {};
    concepts.forEach((_, index) => {
      refs[`concept-${index}`] = React.createRef();
    });
    setNodeRefs(refs);
  }, [concepts]);

  // Initialize positions in a circular pattern around the center
  useEffect(() => {
    if (Object.keys(positions).length === 0) {
      const initialPositions = {};

      // Position concepts evenly in a circle around the central question
      concepts.forEach((_, index) => {
        const angle = (2 * Math.PI * index) / concepts.length;
        initialPositions[`concept-${index}`] = {
          x: Math.cos(angle) * 300,
          y: Math.sin(angle) * 200
        };
      });

      setPositions(initialPositions);
    }
  }, [concepts, positions]);

  // Calculate lines between central question and concepts
  useEffect(() => {
    if (!centralRef.current || !mapRef.current) return;

    const mapRect = mapRef.current.getBoundingClientRect();
    const centralRect = centralRef.current.getBoundingClientRect();

    // Calculate center point of the central question
    const centerX = mapRect.width / 2;
    const centerY = mapRect.height / 2;

    // Width and height of central question
    const centralWidth = centralRect.width;
    const centralHeight = centralRect.height;

    const newLines = concepts.map((_, index) => {
      const nodeRef = nodeRefs[`concept-${index}`]?.current;
      if (!nodeRef) return null;

      const pos = positions[`concept-${index}`];
      if (!pos) return null;

      const nodeRect = nodeRef.getBoundingClientRect();

      // Calculate the angle between central question and concept
      const dx = pos.x;
      const dy = pos.y;
      const angle = Math.atan2(dy, dx);

      // Calculate intersection point with central question box
      let centralEndX, centralEndY;

      // Logic to find where line intersects central question box
      if (Math.abs(Math.cos(angle)) * centralHeight > Math.abs(Math.sin(angle)) * centralWidth) {
        // Line intersects left or right edge of central box
        centralEndX = centerX + Math.sign(Math.cos(angle)) * centralWidth / 2;
        centralEndY = centerY + Math.tan(angle) * Math.sign(Math.cos(angle)) * centralWidth / 2;
      } else {
        // Line intersects top or bottom edge of central box
        centralEndX = centerX + (1/Math.tan(angle)) * Math.sign(Math.sin(angle)) * centralHeight / 2;
        centralEndY = centerY + Math.sign(Math.sin(angle)) * centralHeight / 2;
      }

      // Calculate the position of the concept center relative to map
      const conceptCenterX = centerX + pos.x;
      const conceptCenterY = centerY + pos.y;

      // Width and height of concept
      const conceptWidth = nodeRect.width;
      const conceptHeight = nodeRect.height;

      // Calculate intersection point with concept box
      let conceptEndX, conceptEndY;

      // Logic to find where line intersects concept box
      if (Math.abs(Math.cos(angle)) * conceptHeight > Math.abs(Math.sin(angle)) * conceptWidth) {
        // Line intersects left or right edge of concept box
        conceptEndX = conceptCenterX - Math.sign(Math.cos(angle)) * conceptWidth / 2;
        conceptEndY = conceptCenterY - Math.tan(angle) * Math.sign(Math.cos(angle)) * conceptWidth / 2;
      } else {
        // Line intersects top or bottom edge of concept box
        conceptEndX = conceptCenterX - (1/Math.tan(angle)) * Math.sign(Math.sin(angle)) * conceptHeight / 2;
        conceptEndY = conceptCenterY - Math.sign(Math.sin(angle)) * conceptHeight / 2;
      }

      return {
        centralX: centralEndX,
        centralY: centralEndY,
        conceptX: conceptEndX,
        conceptY: conceptEndY,
        active: activeNodes[index]
      };
    });

    setLines(newLines.filter(Boolean));
  }, [positions, nodeRefs, concepts, activeNodes]);

  // Handle starting to drag a concept
  const handleMouseDown = (e, conceptId) => {
    e.preventDefault();
    e.stopPropagation();

    const rect = e.currentTarget.getBoundingClientRect();
    const mapRect = mapRef.current.getBoundingClientRect();

    setDraggedConcept(conceptId);
    setDragOffset({
      x: e.clientX - (rect.left - mapRect.left),
      y: e.clientY - (rect.top - mapRect.top)
    });

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  // Handle dragging a concept
  const handleMouseMove = (e) => {
    if (!draggedConcept) return;

    e.preventDefault();
    const mapRect = mapRef.current.getBoundingClientRect();
    const newX = e.clientX - mapRect.left - dragOffset.x;
    const newY = e.clientY - mapRect.top - dragOffset.y;

    setPositions(prev => ({
      ...prev,
      [draggedConcept]: { x: newX, y: newY }
    }));
  };

  // Handle dropping a concept
  const handleMouseUp = () => {
    setDraggedConcept(null);
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  // Toggle active/inactive state of a concept when clicked
  const handleConceptClick = (index) => {
    if (draggedConcept) return; // Don't toggle if we're dragging

    setActiveNodes(prev => {
      const newActiveNodes = [...prev];
      newActiveNodes[index] = !newActiveNodes[index];
      return newActiveNodes;
    });
  };

  return (
    <div className="mind-map-container">
      <div 
        className="mind-map" 
        ref={mapRef}
      >
        <svg className="connections">
          {lines.map((line, index) => (
            <line
              key={`line-${index}`}
              x1={line.centralX}
              y1={line.centralY}
              x2={line.conceptX}
              y2={line.conceptY}
              className={!line.active ? 'connection inactive' : 'connection'}
            />
          ))}
        </svg>

        <div className="central-question" ref={centralRef}>
          {centralQuestion}
        </div>

        {concepts.map((concept, index) => {
          const pos = positions[`concept-${index}`] || { x: 0, y: 0 };
          return (
            <div
              key={index}
              className={`concept ${!activeNodes[index] ? 'inactive' : ''} ${draggedConcept === `concept-${index}` ? 'dragging' : ''}`}
              style={{
                transform: `translate(calc(50% + ${pos.x}px), calc(50% + ${pos.y}px))`,
                cursor: draggedConcept === `concept-${index}` ? 'grabbing' : 'grab'
              }}
              ref={nodeRefs[`concept-${index}`]}
              onMouseDown={(e) => handleMouseDown(e, `concept-${index}`)}
              onClick={() => handleConceptClick(index)}
            >
              {concept}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MindMap;
