import React, { useState } from 'react';
import './MindMap.css';

const MindMap = ({ centralQuestion, concepts }) => {
  const [activeNodes, setActiveNodes] = useState(concepts.map(() => true));

  // Toggle active/inactive state of a concept when clicked
  const handleConceptClick = (index, e) => {
    e.stopPropagation();

    setActiveNodes(prev => {
      const newActiveNodes = [...prev];
      newActiveNodes[index] = !newActiveNodes[index];
      return newActiveNodes;
    });
  };

  return (
    <div className="mind-map-container">
      {/* Central node */}
      <div className="central-node">
        <div className="central-question">{centralQuestion}</div>
      </div>

      {/* Top concept */}
      <div className="concept-node concept-top">
        <div className="line line-top"></div>
        <div 
          className={`concept ${!activeNodes[0] ? 'inactive' : ''}`}
          onClick={(e) => handleConceptClick(0, e)}
        >
          {concepts[0]}
        </div>
      </div>

      {/* Left concept */}
      <div className="concept-node concept-left">
        <div className="line line-left"></div>
        <div 
          className={`concept ${!activeNodes[1] ? 'inactive' : ''}`}
          onClick={(e) => handleConceptClick(1, e)}
        >
          {concepts[1]}
        </div>
      </div>

      {/* Right concept */}
      <div className="concept-node concept-right">
        <div className="line line-right"></div>
        <div 
          className={`concept ${!activeNodes[2] ? 'inactive' : ''}`}
          onClick={(e) => handleConceptClick(2, e)}
        >
          {concepts[2]}
        </div>
      </div>

      {/* Bottom left concept */}
      <div className="concept-node concept-bottom-left">
        <div className="line line-bottom-left"></div>
        <div 
          className={`concept ${!activeNodes[3] ? 'inactive' : ''}`}
          onClick={(e) => handleConceptClick(3, e)}
        >
          {concepts[3]}
        </div>
      </div>

      {/* Bottom right concept */}
      <div className="concept-node concept-bottom-right">
        <div className="line line-bottom-right"></div>
        <div 
          className={`concept ${!activeNodes[4] ? 'inactive' : ''}`}
          onClick={(e) => handleConceptClick(4, e)}
        >
          {concepts[4]}
        </div>
      </div>
    </div>
  );
};

export default MindMap;
