import React, { useState } from 'react';
import './MindMap.css';

function MindMap() {
  // State to track which nodes are active/inactive
  const [activeState, setActiveState] = useState({
    personalized: true,
    interactive: true,
    community: true,
    feedback: true,
    accessibility: true
  });

  // Function to toggle the active state of a node
  const toggleNode = (node) => {
    setActiveState(prevState => ({
      ...prevState,
      [node]: !prevState[node]
    }));
  };

  return (
    <div className="mind-map-container">
      {/* Connecting lines */}
      <div className={`line line-top ${!activeState.personalized ? 'inactive' : ''}`}></div>
      <div className={`line line-left ${!activeState.interactive ? 'inactive' : ''}`}></div>
      <div className={`line line-right ${!activeState.community ? 'inactive' : ''}`}></div>
      <div className={`line line-bottom-left ${!activeState.feedback ? 'inactive' : ''}`}></div>
      <div className={`line line-bottom-right ${!activeState.accessibility ? 'inactive' : ''}`}></div>

      {/* Central question */}
      <div className="central-node">
        <div className="central-question">
          How Might We improve online learning experiences?
        </div>
      </div>

      {/* Concept nodes */}
      <div className="concept-node concept-top">
        <div 
          className={`concept ${!activeState.personalized ? 'inactive' : ''}`}
          onClick={() => toggleNode('personalized')}
        >
          Personalized Learning Paths
        </div>
      </div>

      <div className="concept-node concept-left">
        <div 
          className={`concept ${!activeState.interactive ? 'inactive' : ''}`}
          onClick={() => toggleNode('interactive')}
        >
          Interactive Content
        </div>
      </div>

      <div className="concept-node concept-right">
        <div 
          className={`concept ${!activeState.community ? 'inactive' : ''}`}
          onClick={() => toggleNode('community')}
        >
          Community Engagement
        </div>
      </div>

      <div className="concept-node concept-bottom-left">
        <div 
          className={`concept ${!activeState.feedback ? 'inactive' : ''}`}
          onClick={() => toggleNode('feedback')}
        >
          Real-time Feedback
        </div>
      </div>

      <div className="concept-node concept-bottom-right">
        <div 
          className={`concept ${!activeState.accessibility ? 'inactive' : ''}`}
          onClick={() => toggleNode('accessibility')}
        >
          Accessibility Tools
        </div>
      </div>
    </div>
  );
}

export default MindMap;
