import React, { useState } from 'react';
import './MindMap.css';

const MindMap = () => {
  // Track active state for each node
  const [activeNodes, setActiveNodes] = useState({
    top: true,
    left: true,
    right: true,
    bottomLeft: true,
    bottomRight: true
  });

  // Toggle active state for a node
  const toggleNode = (nodeKey) => {
    setActiveNodes(prev => ({
      ...prev,
      [nodeKey]: !prev[nodeKey]
    }));
  };

  return (
    <div className="mind-map-container">
      {/* Central Question */}
      <div className="central-node">
        <div className="central-question">
          How Might We improve online learning experiences?
        </div>
      </div>

      {/* Top Concept */}
      <div className="concept-node concept-top">
        <div 
          className={`concept ${!activeNodes.top ? 'inactive' : ''}`}
          onClick={() => toggleNode('top')}
        >
          Interactive Content
        </div>
      </div>

      {/* Left Concept */}
      <div className="concept-node concept-left">
        <div 
          className={`concept ${!activeNodes.left ? 'inactive' : ''}`}
          onClick={() => toggleNode('left')}
        >
          Community Building
        </div>
      </div>

      {/* Right Concept */}
      <div className="concept-node concept-right">
        <div 
          className={`concept ${!activeNodes.right ? 'inactive' : ''}`}
          onClick={() => toggleNode('right')}
        >
          Personalized Learning
        </div>
      </div>

      {/* Bottom Left Concept */}
      <div className="concept-node concept-bottom-left">
        <div 
          className={`concept ${!activeNodes.bottomLeft ? 'inactive' : ''}`}
          onClick={() => toggleNode('bottomLeft')}
        >
          Gamification
        </div>
      </div>

      {/* Bottom Right Concept */}
      <div className="concept-node concept-bottom-right">
        <div 
          className={`concept ${!activeNodes.bottomRight ? 'inactive' : ''}`}
          onClick={() => toggleNode('bottomRight')}
        >
          Adaptive Feedback
        </div>
      </div>

      {/* Connecting Lines */}
      <div className={`line line-top ${!activeNodes.top ? 'inactive' : ''}`}></div>
      <div className={`line line-left ${!activeNodes.left ? 'inactive' : ''}`}></div>
      <div className={`line line-right ${!activeNodes.right ? 'inactive' : ''}`}></div>
      <div className={`line line-bottom-left ${!activeNodes.bottomLeft ? 'inactive' : ''}`}></div>
      <div className={`line line-bottom-right ${!activeNodes.bottomRight ? 'inactive' : ''}`}></div>
    </div>
  );
};

export default MindMap;
